CREATE TABLE `order_item` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `create_time` datetime DEFAULT NULL,
  `order_id` varchar(255) DEFAULT NULL,
  `price` float DEFAULT NULL,
  `status` bigint(20) DEFAULT NULL,
  `project_id` bigint(20) DEFAULT NULL,
  `user_id` bigint(20) DEFAULT NULL,
  `refund_status` bigint(20) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `FKcstmou1aaysr8ryrh750wfckq` (`project_id`),
  KEY `FKt5mosdtftirppcdhv4wk963m` (`user_id`),
  CONSTRAINT `FKcstmou1aaysr8ryrh750wfckq` FOREIGN KEY (`project_id`) REFERENCES `project` (`id`),
  CONSTRAINT `FKt5mosdtftirppcdhv4wk963m` FOREIGN KEY (`user_id`) REFERENCES `user` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=89 DEFAULT CHARSET=utf8;

